import "./Movie.css";
import { useEffect, useState } from "react";
export default function Movie({
  movieOverview,
  movieRating,
  movieReleaseDate,
  moviePoster,
}) {
  const [infoClass, setInfoClass] = useState(false);

  useEffect(() => console.log(infoClass), [infoClass]);

  return (
    <div
      style={{
        backgroundImage: `url("https://image.tmdb.org/t/p/w300/${moviePoster}")`,
      }}
      className="movie"
    >
      <p className="movie-rating">{movieRating}</p>
      <button
        onClick={() => setInfoClass((anterior) => !anterior)}
        className="infos-button"
      >
        More Infos
      </button>
      <div className={infoClass ? "show-infos" : "hide-infos"}>
        <p className="movie-release">{movieReleaseDate}</p>
        <p className="overview">{movieOverview}</p>
      </div>
    </div>
  );
}
