import "./App.css";
import Movie from "./Movie";
import { useEffect, useState } from "react";
import PageButton from "./PageButton";
import GenreList from "./GenreList";

function App() {
  const [movies, setMovies] = useState([]);
  const [page, setPage] = useState(1);
  const [genres, setGenres] = useState([]);

  async function getMovies() {
    const movies = await fetch(
      `https://api.themoviedb.org/3/discover/movie?api_key=078156dd673bf235bf77c9a7c83d01af&page=${page}&with_genres=`
    ).then((response) => response.json().then((movies) => movies));
    setMovies(movies.results);
  }

  async function getGenres() {
    const genres = await fetch(
      `https://api.themoviedb.org/3/genre/movie/list?api_key=078156dd673bf235bf77c9a7c83d01af`
    ).then((response) => response.json().then((genres) => genres));
    setGenres(genres);
  }

  useEffect(() => {
    getMovies();
  }, []);

  useEffect(() => getMovies(), [page]);

  useEffect(() => getMovies(), [genres]);
  return (
    <div className="App">
      <div className="genre-dropbox-click">
        >
        {genres?.map((genre) => (
          <GenreList genres={genre} />
        ))}
      </div>
      <div className="buttons-container">
        <PageButton
          funcao={() =>
            setPage((anterior) => (anterior > 1 ? anterior - 1 : anterior))
          }
        >
          {"<"}
        </PageButton>
        <PageButton funcao={() => setPage((anterior) => anterior + 1)}>
          {">"}
        </PageButton>
      </div>
      <div className="movies-container">
        {movies?.map((movie) => (
          <Movie
            key={movie.id}
            movieOverview={movie.overview}
            movieRating={movie.vote_average}
            movieReleaseDate={movie.release_date}
            moviePoster={movie.poster_path}
          />
        ))}
      </div>
    </div>
  );
}

export default App;
