import logo from "./logo.svg";
import "./App.css";
import Movie from "./Movie";
import { useEffect, useState } from "react";
import PageButton from "./PageButton";

function App() {
  const [movies, setMovies] = useState([]);
  const [page, setPage] = useState(1);

  async function getApi() {
    const data = await fetch(
      `https://api.themoviedb.org/3/discover/movie?api_key=078156dd673bf235bf77c9a7c83d01af&page=${page}`
    ).then((response) => response.json().then((data) => data));
    setMovies(data.results);
  }

  useEffect(() => {
    getApi();
  }, []);

  useEffect(() => getApi(), [page]);
  return (
    <div className="App">
      <PageButton
        funcao={() =>
          setPage((anterior) => (anterior > 1 ? anterior - 1 : anterior))
        }
      >
        {"<"}
      </PageButton>
      <PageButton funcao={() => setPage((anterior) => anterior + 1)}>
        {">"}
      </PageButton>
      {movies.map((movie, index) => (
        <Movie
          key={index}
          movieName={movie.title}
          movieOverview={movie.overview}
          movieRating={movie.vote_average}
          movieReleaseDate={movie.release_date}
          moviePoster={movie.poster_path}
        />
      ))}
    </div>
  );
}

export default App;
