import "./Movie.css";
export default function Movie({
  movieOverview,
  movieRating,
  movieReleaseDate,
  moviePoster,
}) {
  const [infoClass, setInfoClass] = useState(false);
  setInfoClass((anterior) => !anterior);
  return (
    <div
      style={{
        backgroundImage: `url("https://image.tmdb.org/t/p/w300/${moviePoster}")`,
      }}
      className="movie"
    >
      <p className="movie-rating">{movieRating}</p>
      <button className="infos-button">More Infos</button>
      <div
        onClick={setInfoClass()}
        className={infoClass ? "show-infos" : "hide-infos"}
      >
        <p className="movie-release">{movieReleaseDate}</p>
        <p className="overview">{movieOverview}</p>
      </div>
    </div>
  );
}
